// Given a string 's' containing just the characters 
// '(', ')', '{', '}', '[', ']'
// determine if the input string is valid

// input string is valid if:
// Open brackets must be closed by the same type of brackets
// Open brackets must be closed in the correct order
// Every close ), }, ] bracket has a corresponding open bracket of same type

// Ex1
// input: s = "()"
// output: true

// Ex2
// input: s = "() [] {}"
// output: true

// Ex3
// input: s = "(]"
// output: false

// Examples
// ")" -> invalid
// "(" -> invalid
// "(]" -> invalid
// "([)]" -> invalid
// "()[]{}" -> valid
// "([]){}" -> valid

// Trick
// "([]){}"
// del "(){}"
// del "{}"

// Stack: 
// Can insert an element to the end
// & Remove an element from the end at a constant time
// i (iterate) every single character of String
// string = "([]){}"
// string[0] = "("
// push ")" 
// stack=[")"]

// string[1] = "["
// push "]"
// stack=[")", "]"]

// string[2] = "]"
// we check every element of the stack
// stack=[ ")", "]" ]
// if string[2] = "]" matches stack[1] = "]"
// we've found a MATCH
// we can remove last character from stack
// and move on to next element
// stack = [ ")" ]

// string[3] = ")"
// stack = [ ")" ]
// MATCH
// remove last character from stack
// stack = [ ]

// string[4] = "{"
// push "}" to stack
// stack = [ "}" ]

// string[5] = "}"
// MATCH
// stack = ["}"]
// remove last character "}" from stack 
// stack = []

// if stack = [] -> string is Valid
// if stack != [] -> string is Invalid

// Example of Invalid
// s = "(()"

// s[0] = "("
// stack = [")"]

// s[1] = "("
// stack = [")", ")"]

// s[2] = ")"
// stack = [ ")" ]
// stack = [ ")"] != [ ] -> String is Invalid

// time complexity = 0(N)  Linear
// cuz we iterate through every char in 's'
// space complexity = 0(N) Linear
// 's' is made of purely of Opening brackets
// stack.length == string.length


const s = "([]){}"

const isValid = (s) => {
    // Need a hashMap
    const hashMap = { 
        "(": ")", 
        "{": "}", 
        "[": "]"
    };

    const stack = []

    for(let check of s) {
        console.log('hashmap[check]', hashMap[check])
        if(hashMap[check]) {
            // check = opening bracket 
            // if check ( [ {
            // stack.push ) ] }
            stack.push(hashMap[check])

          // if there's closing brackets in stack=[] &&
          // if stack.indexOf(-1) === s[next]
          // remove Last element from stack=[]
        } else if (stack.length > 0 && stack[stack.length - 1] === check) {
            // s[0] -> (
            // stack=[")"]
            // s[1] -> [
            // stack=[ ")", "]" ]
            // s[2] -> ]
            // stack = [ ")" ]
            // s[3] -> )
            // stack = []
            // s[4] -> {
            // stack = [ "}" ]
            // s[5] -> }
            // stack = []
            stack.pop()
        } else {
            // check if a closing bracket and top of the stack doesn't match
            // stack.length !== s.length
            return false
        }
    }

    return stack.length === 0

}

isValid(s)

